mapper.update:
update design_enroll_judges set screen_status=#{screenStatus}
where id in


    #{id}

mapper.select:
select * from design_enroll_judges where manager_del_status=0
and real_name like CONCAT('%',#{realName},'%')
and top_recommend_status =#{topStatus}

select u.weight,u.realname,p.score_status from design_top_judges_participants p left join sys_user u on
p.judge_id=u.id

    where p.participant_id=#{id}
